export function atualizaFilmes() {
    let filmesSalvos = localStorage.getItem('filmes');

    if(!filmesSalvos) {
        localStorage.setItem('filmes', JSON.stringify(filmes));
    } else {
        filmes = JSON.parse(filmesSalvos);
    }

}

export function buscaFilmes(){
    atualizaFilmes()
    return  filmes
}

export function adicionaFilme(filme) {
    filme.id = parseInt(filmes[filmes.length-1].id) +1
    filmes.push(filme);
    limpaLocalStorage();
    alert('Filme adicionado com sucesso!')
}

export function atualizaFilme(filme) {
    filmes.splice(filme.id - 1, 1, filme);
    limpaLocalStorage();
}

export function deletaFilme(id) {
    filmes = filmes.filter(f => f.id !== parseInt(id));
    limpaLocalStorage();
    alert('Filme excluído com sucesso!')
}

function limpaLocalStorage() {
    localStorage.clear();
    localStorage.setItem('filmes', JSON.stringify(filmes));
}